contracts/Paycrest.sol.content:
//SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.18;
import "@openzeppelin/contracts-upgradeable/utils/cryptography/ECDSAUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import {PaycrestSettingManager} from "./PaycrestSettingManager.sol";
import {IPaycrestStake} from "./interface/IPaycrestStake.sol";
import {IPaycrest, IERC20} from "./interface/IPaycrest.sol";
contract Paycrest is IPaycrest, PaycrestSettingManager { 
    using SafeERC20Upgradeable for IERC20;
    using ECDSAUpgradeable for bytes32;
    struct fee {
        uint256 protocolFee;
        uint256 liquidityProviderAmount;
        uint256 validatorsReward;
    }
    mapping(bytes32 => Order) private order;
    mapping(address => uint256) private _nonce;
    uint256[50] private __gap;
    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(address _usdc) external initializer {    
        _isTokenSupported[_usdc] = true;   
        MAX_BPS = 100_000; 
        protocolFeePercent = 5000; // 5%
        validatorFeePercent = 500; // 0.5%
        __Ownable_init();
    }
    // constructor(address _usdc) {
    //     _isTokenSupported[_usdc] = true;
    // }

    modifier onlyAggregator {
        if(msg.sender != _liquidityAggregator) revert OnlyAggregator();
        _;
    }

    /* ##################################################################
                                USER CALLS
    ################################################################## */
    /** @dev See {createOrder-IPaycrest}. */
    function createOrder(
        address _token, 
        uint256 _amount, 
        bytes32 _institutionCode,
        uint96 _rate, 
        address _senderFeeRecipient,
        uint256 _senderFee,
        address _refundAddress, 
        string calldata messageHash
    )  external returns(bytes32 orderId) {
        // sender must be a whitelisted address
        // if(!_isWhitelisted[msg.sender]) revert NotWhitelisted();
        // checks that are required
        _handler(_token, _amount, _refundAddress, _senderFeeRecipient, _institutionCode);
        // first transfer token from msg.sender
        IERC20(_token).transferFrom(msg.sender, address(this), _amount);
        // increase users nonce to avoid replay attacks
        _nonce[msg.sender] ++;
        // generate transaction id for the transaction
        orderId = keccak256(abi.encode(msg.sender, _nonce[msg.sender]));
        // update transaction
        order[orderId] = Order({
            seller: msg.sender,
            token: _token,
            senderFeeRecipient: _senderFeeRecipient,
            senderFee: _senderFee,
            rate: _rate,
            isFulfilled: false,
            refundAddress: _refundAddress,
            currentBPS: uint96(MAX_BPS),
            amount: _amount
        });
        // emit deposit event
        emit Deposit(_token, _amount, orderId, _rate, _institutionCode, messageHash);
    }

    function _handler(address _token, uint256 _amount, address _refundAddress, address _senderFeeRecipient, bytes32 _institutionCode) internal view {
        // use require for all the custom errors
        require(_isTokenSupported[_token], "TokenNotSupported");
        require(_amount > 0, "AmountIsZero");
        require(_refundAddress != address(0), "ThrowZeroAddress");
        require(_senderFeeRecipient != address(0), "ThrowZeroAddress");
        require(supportedInstitutionsByCode[_institutionCode].name != bytes32(0), "InvalidInstitutionCode");
        // if(!_isTokenSupported[_token]) revert TokenNotSupported();
        // if(_amount == 0) revert AmountIsZero();
        // if(_refundAddress == address(0)) revert ThrowZeroAddress();
        // if(_senderFeeRecipient == address(0)) revert ThrowZeroAddress();
        // if(supportedInstitutionsByCode[_institutionCode].name == bytes32(0)) revert InvalidInstitutionCode();
    }

    /* ##################################################################
                                AGGREGATOR FUNCTIONS
    ################################################################## */
    /** @dev See {settle-IPaycrest}. */
    function settle(
        bytes32 _orderId, 
        address[] calldata _validators, 
        address _liquidityProvider, 
        uint96 _settlePercent
        )  external onlyAggregator() returns(bool) {
        // ensure the transaction has not been fulfilled
        if(order[_orderId].isFulfilled) revert OrderFulfilled();
        // load the token into memory
        address token = order[_orderId].token;
        // substract sum of amount based on the input _settlePercent
        order[_orderId].currentBPS -= _settlePercent;
        // if transaction amount is zero
        if(order[_orderId].currentBPS == 0) {
            // update the transaction to be fulfilled
            order[_orderId].isFulfilled = true;
        }

        // load the fees and transfer associated protocol fees to protocol fee recipient
        ( fee memory _feeParams  ) = _calculateFees(_orderId, _settlePercent);
        uint256 _fee = order[_orderId].senderFee;
        if (_fee > 0) {
            // transfer sender fee
            transferSenderFee(_orderId);
        }
        // transfer protocol fee
        IERC20(token).transfer(feeRecipient, _feeParams.protocolFee);
        // // transfer to liquidity provider 
        IERC20(token).transfer(_liquidityProvider, _feeParams.liquidityProviderAmount);
        IERC20(token).transfer(address(PaycrestStakingContract), (_feeParams.validatorsReward));
        // // distribute rewards
        bool status = IPaycrestStake(PaycrestStakingContract).rewardValidators(
            _orderId,
            token,
            _validators, 
            _feeParams.validatorsReward
        );
        require(status, "UnableToProcessRewards");
        // if(!status) revert UnableToProcessRewards();
        // emit event
        emit Settled(_orderId, _liquidityProvider, _settlePercent);
        return true;
    }

    function transferSenderFee(bytes32 _orderId) internal {
        address recipient = order[_orderId].senderFeeRecipient;
        uint256 _fee = order[_orderId].senderFee;
        // transfer sender fee
        IERC20(order[_orderId].token).transfer(recipient, _fee);
        // emmit event
        emit TransferSenderFee(recipient, _fee);
    }

    /** @dev See {refund-IPaycrest}. */
    function refund(bytes32 _orderId)  external onlyAggregator() returns(bool) {
        // ensure the transaction has not been fulfilled
        require(!order[_orderId].isFulfilled, "OrderFulfilled");
        // if(order[_orderId].isFulfilled) revert OrderFulfilled();
        // reser state values
        order[_orderId].isFulfilled = true;
        order[_orderId].currentBPS = 0;
        // transfer to seller 
        IERC20(order[_orderId].token).transfer(order[_orderId].refundAddress, order[_orderId].amount);
        // emit
        emit Refunded(_orderId);
        return true;
    }

    function _calculateFees(bytes32 _orderId, uint96 _settlePercent) private view returns(fee memory _feeParams ) {
        // get the total amount associated with the orderId
        uint256 amount = order[_orderId].amount;
        // get sender fee from amount
        amount = amount - order[_orderId].senderFee;
        // get the settled percent that is scheduled for this amount
        _feeParams.liquidityProviderAmount = (amount * _settlePercent) / MAX_BPS;
        // deduct protocol fees from the new total amount
        _feeParams.protocolFee = (_feeParams.liquidityProviderAmount * protocolFeePercent) / MAX_BPS; 
        // substract total fees from the new amount after getting the scheduled amount
        _feeParams.liquidityProviderAmount = (_feeParams.liquidityProviderAmount - _feeParams.protocolFee);
        // get primary validators fees primaryValidatorsReward
        _feeParams.validatorsReward = (_feeParams.protocolFee * validatorFeePercent) / MAX_BPS;
        // get primary validators fees secondaryValidatorsReward
        // update new protocol fee
        _feeParams.protocolFee = _feeParams.protocolFee - (_feeParams.validatorsReward);
    }

    /* ##################################################################
                                VIEW CALLS
    ################################################################## */
    /** @dev See {getOrderInfo-IPaycrest}. */
    function getOrderInfo(bytes32 _orderId) external view returns(Order memory) {
        return order[_orderId];
    }

    /** @dev See {isTokenSupported-IPaycrest}. */
    function isTokenSupported(address _token) external view returns(bool) {
        return _isTokenSupported[_token];
    }

    /** @dev See {getSupportedInstitutionName-IPaycrest}. */
    function getSupportedInstitutionName(bytes32 code) external view returns (InstitutionByCode memory) {
        return supportedInstitutionsByCode[code];
    }

    function getSupportedInstitutions(bytes32 currency) external view returns (Institution[] memory) {
        Institution[] memory institutions = supportedInstitutions[currency];
        uint256 length = institutions.length;
        Institution[] memory result = new Institution[](length);

        for (uint256 i = 0; i < length; ) {
            result[i] = institutions[i];
            unchecked {
                i++;
            }
        }

        return result;
    }

    /** @dev See {getFeeDetails-IPaycrest}. */
    function getFeeDetails() external view returns(
        uint128, 
        uint128, 
        uint256
    ) {
        return(protocolFeePercent, validatorFeePercent, MAX_BPS);
    }

    /** @dev See {getLiquidityAggregator-IPaycrest}. */
    function getLiquidityAggregator() external view returns(address) {
        return _liquidityAggregator;
    }

    /** @dev See {getWhitelistedStatus-IPaycrest}. */
    function getWhitelistedStatus(address sender) external view returns(bool) {
        return _isWhitelisted[sender];
    }

    // DECLARE A FUNCTION TO WITHDRAW ANY TOKEN FROM CONTRACT CAN ONLY NE CALLED BY OWNER
    function withdraw(address _token, address _recipient, uint256 _amount) external onlyOwner {
        IERC20(_token).transfer(_recipient, _amount);
    }

}

contracts/PaycrestSettingManager.sol.content:
//SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.18;
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";

contract PaycrestSettingManager is OwnableUpgradeable { 
    struct Institution {
        bytes32 code; // usually not more than 8 letters
        bytes32 name; // 
    }
    struct InstitutionByCode {
        bytes32 name;
        bytes32 currency;
    }
    uint256 internal MAX_BPS;
    uint128 internal protocolFeePercent; // 5%
    uint128 internal validatorFeePercent; // 0.5%
    address internal feeRecipient;
    address internal PaycrestStakingContract;
    address internal _liquidityAggregator;

    mapping(address => bool) internal _isTokenSupported;
    mapping(address => bool) internal _isWhitelisted;

    mapping(bytes32 => Institution[]) internal supportedInstitutions;
    mapping(bytes32 => InstitutionByCode) internal supportedInstitutionsByCode;

    /// @notice Revert when zero address is passed in
    error ThrowZeroAddress();
    /// @notice Revert when zero address is passed in
    error ThrowZeroValue();
    /// @notice Revert when zero address is passed in
    error InvalidParameter(bytes32 what);
    /// @notice Revert when invalid token is provided
    error TokenNotSupported();
    /// @notice Revert when input amount is zero
    error AmountIsZero();

    event SettingManagerBool(bytes32 what, address value, bool status);
    event SettingManagerForInstitution(bytes32 what, bytes8 value, bytes8 status);
    event PaycrestFees(uint128 protocolFee, uint128 validatorFeePercent);

    /* ##################################################################
                                OWNER FUNCTIONS
    ################################################################## */
    function settingManagerBool(bytes32 what, address value, bool status) external onlyOwner {
        if (value == address(0)) revert ThrowZeroAddress();
        if (what == "token") _isTokenSupported[value] = status;
        if (what == "whitelist") _isWhitelisted[value] = status;
        else revert InvalidParameter(what);
        emit SettingManagerBool(what, value, status);
    }

    function setSupportedInstitutions(bytes32 currency, Institution[] memory institutions) external onlyOwner { 
        uint256 length = institutions.length;
        for (uint i = 0; i < length; ) {
            supportedInstitutions[currency].push(institutions[i]);
            supportedInstitutionsByCode[institutions[i].code] = InstitutionByCode({
                name: institutions[i].name, currency: currency
            });
            unchecked {
                i++;
            }
        }
    }

    function updateProtocolFees(uint128 _protocolFeePercent, uint128 _validatorFeePercent) external onlyOwner {
        protocolFeePercent = _protocolFeePercent;
        validatorFeePercent = _validatorFeePercent;
        emit PaycrestFees(_protocolFeePercent, _validatorFeePercent);
    }

    function updateFeeRecipient(bytes32 what, address value) external onlyOwner {
        if (value == address(0)) revert ThrowZeroAddress();
        if (what == "fee") feeRecipient = value;
        if (what == "aggregator") _liquidityAggregator = value;
        else if (what == "stake") PaycrestStakingContract = value;
    }

}

contracts/PaycrestValidator.sol.content:
//SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.18;
import  "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";

contract PaycrestValidator is OwnableUpgradeable, ReentrancyGuardUpgradeable { 
    using SafeERC20Upgradeable for IERC20Upgradeable;

    address private Paycrest;    
    bool private initialization;
    mapping(address => uint256) private minimumAmount;
    mapping(address => mapping (address => uint256)) private _balance;
    uint256[50] private __gap;

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(address _paycrest) external initializer {    
        Paycrest = _paycrest;   
        initialization = true;
        __Ownable_init();
        __ReentrancyGuard_init();
    }

    event NewTokenSupported(address indexed token, uint256 indexed minimumStakeAmount);
    event Initialized(bool indexed status);
    event Staked(address indexed user, uint256 indexed amount);
    event Withdrawn(address indexed user, uint256 indexed amount);
    event RewardValidators(
        bytes32 orderId, 
        address token, 
        address indexed validator, 
        uint256 indexed validatorsReward
    );

    error ThrowInitPaused();
    error TokenNotSupported();
    error MinimumRequired();
    error Insufficient();
    error ThrowPaycrest();

    modifier OnlyPaycrest() {
        if(msg.sender != Paycrest) revert ThrowPaycrest();
        _;
    }

    function setMinimumAmountForTokens(address _token, uint256 minimumStakeAmount) external onlyOwner {
        minimumAmount[_token] = minimumStakeAmount;
        emit NewTokenSupported(_token, minimumStakeAmount);
    }

    function initialized(bool status) external onlyOwner {
        initialization = status;
        emit Initialized(status);
    }

    function stake(address token, uint256 amount)  external {
        if(!initialization) revert ThrowInitPaused();
        if(!_isTokenSupported(token)) revert TokenNotSupported();
        if(amount < minimumAmount[token]) revert MinimumRequired();
        IERC20Upgradeable(token).transferFrom(msg.sender, address(this), amount);
        _balance[msg.sender][token] += amount;
        emit Staked(msg.sender, amount);
    }

    function withdraw(uint256 amount, address token, address recipient) external nonReentrant() {
        uint256 previouslyStakedAmount = _balance[msg.sender][token];
        if(amount > previouslyStakedAmount) revert Insufficient();
        _balance[msg.sender][token] -= amount;
        IERC20Upgradeable(token).transfer(recipient, amount);
        emit Withdrawn(msg.sender, amount);
    }

    function rewardValidators(
        bytes32 orderId, 
        address token, 
        address[] memory validators,
        uint256 validatorsReward
    ) external OnlyPaycrest() returns(bool) {
        uint256 lengthOfAllValidators = validators.length;
        uint256 validatorsShares = validatorsReward / lengthOfAllValidators;
        for(uint256 i = 0; i < lengthOfAllValidators; ) {
            _balance[validators[i]][token] += validatorsShares;
            emit RewardValidators(orderId, token, validators[i], validatorsShares);
            unchecked {
                i++;
            }
        }
        return true;
    }


    function _isTokenSupported(address token) private view returns(bool) {
        (, bytes memory result) = Paycrest.staticcall(abi.encodeWithSignature("isTokenSupported(address)", token));
        return abi.decode(result, (bool));
    }

    function getPaycrest() external view returns(address) {
        return Paycrest;
    }

    function getInitializationState() external view returns(bool) {
        return initialization;
    }

    function getMinimumAmountRequiredFor(address token) external view returns(uint256) {
        return minimumAmount[token];
    }

    function getValidatorInfo(address user, address token) external view returns(uint256) {
        return _balance[user][token];
    }

}

